mapper.select:
SELECT COUNT(*)
FROM Enrollment
WHERE ENROLLMENT_STUDENT_ID = #{studentId}
  AND ENROLLMENT_LECTURE_ID = #{lectureId}

mapper.insert:
INSERT INTO Enrollment (
    ENROLLMENT_STUDENT_ID,
    ENROLLMENT_LECTURE_ID
) VALUES (
             #{studentId},
             #{lectureId}
         )

mapper.delete:
DELETE FROM Enrollment
WHERE ENROLLMENT_STUDENT_ID = #{studentId}
  AND ENROLLMENT_LECTURE_ID = #{lectureId}
